import logo from "./logo.svg";
import React, { useEffect, useState } from "react";
import { Form, Card, Image, Icon, Grid } from "semantic-ui-react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [userName, setUserName] = useState("");
  const [repos, setRepos] = useState("");
  const [org, setOrg] = useState("");
  const [image, setImage] = useState("");
  const [userInput, setUserInput] = useState("");
  const [error, setError] = useState(null);
  const [error1, setError1] = useState(null);
  useEffect(() => {
    fetch("https://api.github.com/users/example")
      .then((res) => res.json())
      .then((data) => {
        setData(data);
      });
  }, []);

  const setData = ({ name, login, public_repos, company, avatar_url }) => {
    setName(name);
    setUserName(login);
    setRepos(public_repos);
    setOrg(company);
    setImage(avatar_url);
  };

  const handleSearch = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = () => {
    fetch(`https://api.github.com/users/${userInput}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.message) {
          setError(data.message);
        } else {
          setData(data);
          setError(null);
        }
      });
  };

  return (
    <Grid columns={2} verticalAlign="middle" centered>
      <Grid.Column
        style={{
          paddingBottom: "0px",
          paddingLeft: "0px",
          paddingRight: "0px",
        }}
      >
        <div className="card">
          <Form onSubmit={handleSubmit}>
            <Form.Group>
              <Form.Input
                placeholder="Enter the username"
                name="user"
                onChange={handleSearch}
              />
              <Form.Button content="Search" />
            </Form.Group>
          </Form>
        </div>
      </Grid.Column>
      <Grid.Column
        style={{
          paddingBottom: "0px",
          paddingLeft: "0px",
          paddingRight: "0px",
        }}
      >
        {error ? (
          <div className="error">
            {" "}
            <h1>{error}</h1>{" "}
          </div>
        ) : (
          <div className="card">
            <Card style={{ height: "fit-content" }}>
              <Image src={image} wrapped ui={false} />
              <Card.Content style={{ height: "100px" }}>
                <Card.Header>{name}</Card.Header>
                <Card.Meta>{userName}</Card.Meta>
                <Card.Description />
              </Card.Content>
              <Card.Content extra>
                <a>
                  <Icon name="user" />
                  Organisation: {org}
                </a>
              </Card.Content>
              <Card.Content extra>
                <a>
                  <Icon name="user" />
                  List of Repositories: {repos}
                </a>
              </Card.Content>
            </Card>
          </div>
        )}
      </Grid.Column>
    </Grid>
  );
}

export default App;
