import logo from './logo.svg';
import React, { useEffect, useState } from "react";
import { Form, Card, Image, Icon } from 'semantic-ui-react';
import './App.css';

function App() {
  const [name, setName] = useState('');
  const [userName, setUserName] = useState('');
  const [repos, setRepos] = useState('');
  const [org, setOrg] = useState('');
  const [image, setImage] = useState('');
  const [userInput, setUserInput] = useState('');
  const [error, setError] = useState(null);
  const [error1, setError1] = useState(null);
  useEffect(() => {
    fetch("https://api.github.com/users/example")
    .then(res => res.json())
    .then(data => {
      setData(data);
      });
  }, []);

  const setData = ({ name, login, public_repos, company, avatar_url }) => {
    setName(name)
    setUserName(login)
    setRepos(public_repos)
    setOrg(company)
    setImage(avatar_url)
  };

  const handleSearch = (e) => {
    setUserInput(e.target.value)
  }

  const handleSubmit = () => {
    fetch(`https://api.github.com/users/${userInput}`)
    .then(res => res.json())
    .then(data => {
      if(data.message){
        setError(data.message);
      }
      else{
        setData(data);
        setError(null);
      }  
    })
  }


  return (
    <div>
      <div className = 'navbar'>Steal from Github</div>
      <div className = 'search'>
      <Form onSubmit={handleSubmit}>
          <Form.Group>
            <Form.Input
              placeholder='Enter the username'
              name='user'
              onChange={handleSearch}
            />
            <Form.Button content='Search' />
          </Form.Group>
        </Form>
      </div>
      { error ? (<div className = 'error'> <h1>{error}</h1> </div> ): (<div className = 'card'>
      <Card>
      <Image src={image} wrapped ui={false} />
    <Card.Content>
      <Card.Header>Name: {name}</Card.Header>
      <Card.Header>User Name: {userName}</Card.Header>

    </Card.Content>
    <Card.Content extra>
      <a>
        <Icon name='user' />
        Organisation: {org}
      </a>
    </Card.Content>
    <Card.Content extra>
      <a>
        <Icon name='user' />
        List of Repositories: {repos} 
      </a>
    </Card.Content>
  </Card>

      </div>)}

    </div>
  );
}

export default App;
